const express = require("express");
const bcrypt = require("bcrypt");
const User = require("../controllers/User");
const router = express.Router();
const jwt = require("jsonwebtoken");
const jwtSecret = "90204520430234";

let initWebRoutes = (app) => {
  router.post("/register", async (req, res) => {
    const { name, email, password } = req.body;
    try {
      const salt = await bcrypt.genSalt(12);
      const hashedPassword = await bcrypt.hash(password, salt);

      const userDoc = await User.create({
        name,
        email,
        password: hashedPassword,
      });

      res.json(userDoc);
    } catch (error) {
      console.error(error);
      res.status(500).json({ error: "Internal server error" });
    }
  });

  router.post("login", async (req, res) => {
    const { email, password } = req.body;
    try {
      const userdoc = await User.findOne({ email });
      if (userdoc) {
        const passwordOk = await bcrypt.compare(userdoc.password, password);
        if (passwordOk) {
          jwt.sign(
            { email: userdoc.email, id: userdoc._id, name: userdoc.name },
            jwtSecret,
            {},
            (err, token) => {
              if (err) throw err;
              console.log("check userdoc", userdoc);
              res.cookie("token", token).status(200).json(userdoc);
            }
          );
        } else {
          res.status(401).json({ err: "Incorrect password" });
        }
      } else {
        res.status(404).json({ err: "User not found" });
      }
    } catch (e) {
      console.log(e);
      res.status(500).json({ err: "Interial sever error" });
    }
  });

  router.get("/profile", (req, res) => {
    const { token } = req.cookies;
    if (token) {
      jwt.verify(token, jwtSecret, {}, async (err, user) => {
        const { name, email, _id } = await User.findById(user.id);
        res.status(200).json({ name, email, _id });
      });
    } else {
      res.status(500).json(null);
    }
  });

  router.get("/logout", (req, res) => {
    res.cookie("token", "").json(true);
  });

  return app.use("/", router);
};

module.exports = initWebRoutes;

